package com.jastipapp.navigation;

import android.Manifest;
import android.bluetooth.BluetoothDevice;
import android.content.Context;
import android.content.pm.PackageManager;
import android.graphics.Bitmap;
import android.graphics.BitmapFactory;
import android.os.Build;
import android.support.v7.app.AppCompatActivity;
import android.os.Bundle;
import android.util.Base64;
import android.util.Log;
import android.view.View;
import android.widget.Button;

import java.util.List;

import cn.paperang.sdk.client.PaperangApi;
import cn.paperang.sdk.client.errcode.DevConnStatus;
import cn.paperang.sdk.btclient.callback.OnInitStatusListener;
import cn.paperang.sdk.btclient.callback.OnBtDeviceListener;
import cn.paperang.sdk.btclient.callback.OnBtStatusChangeListener;
import cn.paperang.sdk.btclient.model.PaperangDevice;

public class PrintPaperangActivity extends AppCompatActivity {

    public static final String TAG = "PrintPaperangActivity";

    public Context mContext;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_print_paperang);

        try {

            this.mContext = this;
            if (Build.VERSION.SDK_INT >= 23) {
                if (checkSelfPermission(Manifest.permission.ACCESS_COARSE_LOCATION) == PackageManager.PERMISSION_GRANTED) {
                    //Log.d(TAG,"Context" + this.getApplicationContext().toString());

                    PaperangApi.init(mContext.getApplicationContext(),"th.muangthai.aTR", new OnInitStatusListener() {
                        @Override
                        public void initStatus(boolean b) {
                            Log.d(TAG, "Paperang init succeeded");
                        }
                    });
                    PaperangApi.registerBT(mContext.getApplicationContext());
                }
            }


        } catch (Exception e) {
            Log.e(TAG, e.toString());

        }

        Button btnRegisterBT = (Button) findViewById(R.id.btnRegisterBT);

        btnRegisterBT.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                runOnUiThread(new Runnable() {
                    @Override
                    public void run() {
                        boolean b = PaperangApi.initBT(mContext.getApplicationContext());
                        if (b) {
                            Log.d(TAG, "InitBT succeeded");
                        } else {
                            Log.d(TAG, "InitBT failed");
                        }
                    }
                });

            }
        });

        Button btnConnectToPrinter = (Button) findViewById(R.id.btnConnectToPrinter);

        btnConnectToPrinter.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {

                runOnUiThread(new Runnable() {
                    @Override
                    public void run() {
                        PaperangApi.connBT("00:15:83:DB:80:88", 10000, new OnBtDeviceListener() {
                            @Override
                            public void onBtConnSuccess(BluetoothDevice bluetoothDevice, int i) {
                                //super.onBtConnSuccess(bluetoothDevice, i);
                                Log.d(TAG, "Paperang connected.");

                            }

                            @Override
                            public void onBtConnFailed(int i, String s) {
                                //super.onBtConnFailed(i, s);
                                Log.d(TAG, "Paperang failed to connect." + s);
                            }

                            @Override
                            public void onDiscoveryTimeout() {
                                //super.onDiscoveryTimeout();
                                Log.d(TAG, "Connection timeout");
                            }
                        });
                    }
                });


            }
        });

        Button btnPaperangTestPrint = (Button) findViewById(R.id.btnPaperangTestPrint);

        btnPaperangTestPrint.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {

                runOnUiThread(new Runnable() {
                    @Override
                    public void run() {
                        String base64String = "";
                        byte[] decodedString = Base64.decode(base64String, Base64.DEFAULT);
                        Bitmap decodedByte = BitmapFactory.decodeByteArray(decodedString, 0, decodedString.length);
                        PaperangApi.sendImgToBT(mContext.getApplicationContext(), decodedByte, new OnBtDeviceListener() {
                            @Override
                            public void onBtDataSendFinish() {
                                super.onBtDataSendFinish();
                                Log.d(TAG, "Finished printing");
                            }

                            @Override
                            public void onBtDataSendFailed(int i, String s) {
                                super.onBtDataSendFailed(i, s);
                                Log.d(TAG, "Failed printing");
                            }
                        });
                    }
                });

            }
        });

        Button btnDisConnectFromPrinter = (Button) findViewById(R.id.btnDisconnectFromPrinter);

        btnDisConnectFromPrinter.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                runOnUiThread(new Runnable() {
                    @Override
                    public void run() {
                        PaperangApi.disconnBT();
                    }
                });

            }
        });

        Button btnScan = (Button) findViewById(R.id.btnScan);

        btnScan.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                runOnUiThread(new Runnable() {
                    @Override
                    public void run() {
                        PaperangApi.setAutoConnect(true);
                        PaperangApi.searchBT(new OnBtDeviceListener() {
                            @Override
                            public void onBtFound(List<PaperangDevice> list) {
                                super.onBtFound(list);
                                Log.d(TAG, "Device found");
                            }

                            @Override
                            public void onDiscoveryTimeout() {
                                super.onDiscoveryTimeout();
                                Log.d(TAG, "Device not found");
                            }
                        });
                    }
                });
            }
        });


    }

    @Override
    protected void onDestroy() {
        super.onDestroy();
        PaperangApi.unregisterBT(this);
    }
}
